import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Field, reduxForm } from 'redux-form';
import { Link, withRouter } from 'react-router-dom';
import { renderInput } from '../../utils/ReduxForm';
import {editSample} from '../../actions/basespace'

class EditForm extends Component {
  componentDidMount() {
    const { data } = this.props.data;
    this.props.initialize({
       biosample_id: data.biosample_id,
         biosample_type: data.biosample_type,
           biosample_name: data.biosample_name,
           biosample_path: data.biosample_path,
           library_id: data.library_id,
           biosample_created_on: data.biosample_created_on,
           project_id: data.project_id,
    });
  }

  formSubmit(formValue) {
    const { data } = this.props.data;
    // console.log(data.id);
    this.props.editPost(formValue, data.id, () => {
      this.props.history.push('#');
    });
  }

  render() {
    const { handleSubmit } = this.props;
    const { data } = this.props.data;

    return (
      <form onSubmit={handleSubmit(this.formSubmit.bind(this))}>
        <Field component={renderInput} label="Bio Sample Id" type="text" name="biosample_id" />
        <Field
          component={renderInput}
          label="biosample_type"
          type="text"
          name="biosample_type"
        />
        <Field
          component={renderInput}
          label="biosample_name"
          type="text"
          name="biosample_name"
        />
        <Field
          component={renderInput}
          label="biosample_path"
          type="text"
          name="biosample_path"
        />
        <Field
          component={renderInput}
          label="library_id"
          type="text"
          name="library_id"
        />
        <Field
          component={renderInput}
          label="biosample_created_on"
          type="date"
          name="biosample_created_on"
        />
        <Field
          component={renderInput}
          label="project_id"
          type="text"
          name="project_id"
        />
        <div className="form-group">
          <button className="btn btn-primary" type="submit">
            Save
          </button>
          <Link to={'#'} className="btn btn-default">
            {' '}
            Cancel
          </Link>
        </div>
      </form>
    );
  }
}

EditForm = withRouter(EditForm);
EditForm = reduxForm({
  form: 'EditForm',
  fields: ['biosample_id',
            'biosample_type',
           'biosample_name',
           'biosample_path',
            'library_id',
            'biosample_created_on',
            'project_id']
})(EditForm);

export default connect(
  null,
  { editSample }
)(EditForm);
