import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Modal from '../Modal';
import history from '../../history'
import { getProjectByID, deleteProject } from '../../actions/basespace'

class DeleteProject extends Component {
  componentDidMount() {
    this.props.getProjectByID(this.props.match.params.id);
  }

  renderContent() {
    if (!this.props.project) {
      return 'Are you sure you want to delete this Project?';
    }
    return `Are you sure you want to delete the Project: ${this.props.project.id}`;
  }

  renderActions() {
    const { id } = this.props.match.params;
    return (
      <Fragment>
        <button
          onClick={() => this.props.deleteProject(id)}
          className='ui negative button'
        >
          Delete
        </button>
        <Link to='/' className='ui button'>
          Cancel
        </Link>
      </Fragment>
    );
  }

  render() {
    return (
      <Modal
        title='Delete Project'
        content={this.renderContent()}
        actions={this.renderActions()}
        onDismiss={() => history.push('/')}
      />
    );
  }
}

const mapStateToProps = (state, ownProps) => ({
  project: state.projects[ownProps.match.params.id]
});

export default connect(
  mapStateToProps,
  { getProjectByID, deleteProject }
)(DeleteProject);
