import React, { Component, Fragment } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { Form } from 'react-bootstrap';
import { ToastsContainer, ToastsStore } from 'react-toasts';
import { IoMdTrash } from "react-icons/io";
import { MdDoneAll, MdPersonAdd } from "react-icons/md";
import { TiPencil, TiUpload, TiArrowSync } from "react-icons/ti";
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

export default class Company extends Component {

    constructor(props) {
        super(props);
        this.onChangebiosample_id = this.onChangebiosample_id.bind(this);
        this.onChangebiosample_type = this.onChangebiosample_type.bind(this);
        this.onChangebiosample_name = this.onChangebiosample_name.bind(this);
        this.onChangebiosample_path = this.onChangebiosample_path.bind(this);
        this.onChangelibrary_id = this.onChangelibrary_id.bind(this);
        this.onChangebiosample_created_on = this.onChangebiosample_created_on.bind(this);
        this.onChangeproject_id = this.onChangeproject_id.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            biosample_id:'' ,
            biosample_type: '' ,
            biosample_name:''  ,
            biosample_path:  '',
            library_id: '',
            biosample_created_on: '' ,
            project_id: '',
        }
    }
    onChangebiosample_id(e) {
        this.setState({
            biosample_id: e.target.value
        });
    }
    onChangebiosample_type(e) {
        this.setState({
            biosample_type: e.target.value
        })
    }
    onChangebiosample_name(e) {
        this.setState({
            biosample_name: e.target.value
        })
    }

    onChangebiosample_path(e) {
        this.setState({
            biosample_path: e.target.value
        })
    }

    onChangelibrary_id(e) {
        this.setState({
            library_id: e.target.value
        })
    }

    onChangebiosample_created_on(e) {
        this.setState({
            biosample_created_on: e.target.value
        })
    }

    onChangeproject_id(e) {
        this.setState({
            project_id: e.target.value
        })
    }



    getDatas() {
        axios.get(`http://127.0.0.1:8000/api/biosample/`)
            .then(res => {
                this.setState({
                    companyList: res.data,
                    isLoading: false
                });
            })
    }

    componentDidMount() {
        this.getDatas();
    }

    reset = () => {
        this.setState({
            biosample_id:'' ,
            biosample_type: '' ,
            biosample_name:''  ,
            biosample_path:  '',
            library_id: '',
            biosample_created_on: '' ,
            project_id: '',
            companyList: []
        })
    }

    onSubmit(e) {
        e.preventDefault();
        const obj = {

            biosample_id:this.state.biosample_id ,
            biosample_type: this.state.biosample_type,
            biosample_name:this.state.biosample_name  ,
            biosample_path: this.state.biosample_path,
            library_id: this.state.library_id,
            biosample_created_on: this.state.biosample_created_on,
            project_id:this.state.project_id,
        };
        if (this.state.id) {
            axios.put('http://127.0.0.1:8000/api/biosample' + `/${this.state.id}/`, obj)
                .then(res => {
                    ToastsStore.success('Successfully Updated!');
                    this.getDatas();
                });
        } else {
            axios.post('http://127.0.0.1:8000/api/biosample/', obj)
                .then(res => {
                    ToastsStore.success('Successfully Saved!');
                    this.getDatas();
                });
        }
        this.setState({
            biosample_id:'' ,
            biosample_type: '' ,
            biosample_name:''  ,
            biosample_path:  '',
            library_id: '',
            biosample_created_on: '' ,
            project_id: '',
        })
    }

    editRow = ( biosample_id,biosample_type,biosample_path,biosample_created_on,library_id,project_id,biosample_name) => {
        this.setState({
            biosample_id:biosample_id ,
            biosample_type: biosample_type ,
            biosample_name: biosample_name ,
            biosample_path:biosample_path ,
            library_id: library_id,
            biosample_created_on: biosample_created_on,
            project_id:project_id,
        })
    }

    deleteRow = (id) => {
        axios.delete('http://127.0.0.1:8000/api/biosample' + `/${id}/`)
            .then(res => {
                ToastsStore.warning('Successfully Deleted!');
                this.getDatas();
            });
    }


    render() {
        const { isLoading, companyList } = this.state;

        return (
            <div>
                <div className="container">
                    <div className="text-center">
                        <h2 className="jumbotron">Company Details</h2>
                    </div>
                    <div className="row">
                        <div className="col">
                            <div className="row">
                                <div className="col-md-6">
                                <form className="forms-sample" onClick={this.submitForm}>
                <Form.Group>
                  <label htmlFor="exampleInputUsername1">BioSample Id</label>
                  <Form.Control
                    type="text"
                    id="exampleInputUsername1"
                    placeholder="project Name"
                    size="lg"
                    value={this.state.biosample_id}
                    name="biosample_id"
                    onChange={this.onChangebiosample_id}
                    required
                  />
                </Form.Group>
                <Form.Group>
                  <label htmlFor="exampleInputUsername1">BioSample Type</label>
                  <Form.Control
                    type="text"
                    id="exampleInputUsername1"
                    placeholder="project Name"
                    size="lg"
                    value={this.state.biosample_type}
                    name="biosample_type"
                    onChange={this.onChangebiosample_type}
                    required
                  />
                </Form.Group>
                <Form.Group>
                  <label id="inputGroupFile01">BioSample Name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="inputGroupFile01"
                    required
                    value={this.state.biosample_name}
                    name="biosample_name"
                    onChange={this.onChangebiosample_name}
                  />
                </Form.Group>
                <Form.Group>
                  <label id="inputGroupFile02">BioSample Path</label>
                  <input
                    type="text"
                    className="form-control"
                    id="inputGroupFile02"
                    value={this.state.biosample_path}
                    name="biosample_path"
                    onChange={this.onChangebiosample_path}
                    required
                  />
                </Form.Group>
                <Form.Group>
                  <label id="inputGroupFile03">Library Id</label>
                  <input
                    type="text"
                    className="form-control"
                    id="inputGroupFile03"
                    value={this.state.library_id}
                    name="library_id"
                    onChange={this.onChangelibrary_id}
                    required
                  />
                </Form.Group>
                <Form.Group>
                  <label id="inputGroupFile04">BioSample Created On</label>
                  <input
                    type="date"
                    className="form-control"
                    id="inputGroupFile04"
                    value={this.state.biosample_created_on}
                    name="biosample_created_on"
                    onChange={this.onChangebiosample_created_on}
                    required
                  />
                </Form.Group>

                <Form.Group>
                  <label id="inputGroupFile04">Project Id</label>
                  <input
                    type="text"
                    className="form-control"
                    id="inputGroupFile04"
                    value={this.state.project_id}
                    name="project_id"
                    onChange={this.onChangeproject_id}
                    required
                  />
                </Form.Group>

                <div className="form-group">
                                            <button className="btn btn-primary" type="submit" >
                                                Submit <MdDoneAll />
                                            </button>
                                            <button onClick={this.reset} className="btn btn-secondary" type="button" >
                                                <TiArrowSync />
                                                Reset
                                            </button>
                                        </div>
              </form> 
                                </div>
                                <div className="col-md-6">
                                    {/* <h6 className="text-center">Registered Companies</h6><br /> */}
                                    {/* <div>
                                        { 
                                        if(!isLoading)
                                            return (
                                            <h6 className="text-center alert alert-danger" role="alert">message</h6>
                                        );
                                        }
                                    </div> */}

                                    <div>
                                        {isLoading ? (
                                            <h6 className="text-center alert alert-danger" role="alert">Loading...</h6>
                                        ) : (
                                                <h6 className="text-center">Registered Companies</h6>
                                            )}
                                    </div>
                                    <br></br>

                                    {/* <h6 className="text-center alert alert-danger" role="alert">message</h6><br /> */}
                                    <table className="table table-sm table-hover">
                                        <tbody>
                                            {(companyList.map((item, i) => {
                                                if (!isLoading)
                                                    return [
                                                        <Fragment>
                                                            <tr key={i}>
                                                                <td>{item.biosample_id}</td>
                                                                <td>{item.biosample_name}</td>
                                                                <td>{item.biosample_path}</td>
                                                                <td>{item.biosample_type}</td>
                                                                <td>{item.biosample_created_on}</td>
                                                                <td>{item.library_id}</td>
                                                                <td>{item.project_id}</td>
                                                                <td>
                                                                    <a className="btn">
                                                                        <TiUpload />
                                                                        <input hidden type="file" name="logo" accept="image/*" />
                                                                    </a>
                                                                    <a className="btn" onClick={(e) => this.editRow(item.biosample_id, item.biosample_name, item.biosample_path, item.biosample_type,item.library_id,item.project_id, item.biosample_created_on,e)}>
                                                                        <TiPencil />
                                                                    </a>
                                                                    <a className="btn" onClick={(e) => this.deleteRow(item.id, e)}>
                                                                        <IoMdTrash />
                                                                    </a>
                                                                    <Link to={'/basespace/projects/addproject' + item.id}>
                                                                        <a className="btn">
                                                                            <MdPersonAdd />
                                                                        </a>
                                                                    </Link>
                                                                </td>
                                                            </tr>
                                                        </Fragment>
                                                    ];
                                            })
                                            )}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                    <ToastsContainer store={ToastsStore} />
                </div>
            </div>
        )
    }
}
