import React from "react";
import { Form } from "react-bootstrap";
import { Link } from "react-router-dom";
import {EditBiosample} from '../../actions/basespace'
class Update extends React.Component {
  constructor(props) {
    super(props);
    this.onChangebiosample_id = this.onChangebiosample_id.bind(this);
    this.onChangebiosample_type = this.onChangebiosample_type.bind(this);


    this.onChangebiosample_name = this.onChangebiosample_name.bind(this);
    this.onChangebiosample_path = this.onChangebiosample_path.bind(this);
    this.onChangelibrary_id = this.onChangelibrary_id.bind(this);
    this.onChangebiosample_created_on = this.onChangebiosample_created_on.bind(this);
    this.onChangeproject_id = this.onChangeproject_id.bind(this);
    this.getID = this.getID.bind(this);







    this.updateTutorial = this.updateTutorial.bind(this);







    this.state = {
      id:null,
      biosample_id:'' ,
      biosample_type: '' ,
      biosample_name:''  ,
      biosample_path:  '',
      library_id: '',
      biosample_created_on: '' ,
      project_id: '',
    };

  }



  componentDidMount() {
    this.getID(this.props.match.params.id);
  }





  onChangebiosample_id(e) {
    const biosample_id = e.target.value;

    this.setState(function(prevState) {
      return {
        currentValue: {
          ...prevState.currentValue,
          biosample_id: biosample_id
        }
      };
    });
  }

  onChangebiosample_name(e) {
    const biosample_name = e.target.value;

    this.setState(function(prevState) {
      return {
        currentValue: {
          ...prevState.currentValue,
          biosample_name: biosample_name
        }
      };
    });
  }

  onChangebiosample_path(e) {
    const biosample_path = e.target.value;

    this.setState(function(prevState) {
      return {
        currentValue: {
          ...prevState.currentValue,
          biosample_path: biosample_path
        }
      };
    });
  }

  onChangebiosample_type(e) {
    const biosample_type = e.target.value;

    this.setState(function(prevState) {
      return {
        currentValue: {
          ...prevState.currentValue,
          biosample_type: biosample_type
        }
      };
    });
  }


  onChangebiosample_created_on(e) {
    const biosample_created_on = e.target.value;

    this.setState(function(prevState) {
      return {
        currentValue: {
          ...prevState.currentValue,
          biosample_created_on: biosample_created_on
        }
      };
    });
  }


  onChangelibrary_id(e) {
    const library_id = e.target.value;

    this.setState(function(prevState) {
      return {
        currentValue: {
          ...prevState.currentValue,
          library_id: library_id
        }
      };
    });
  }

  onChangeproject_id(e) {
    const project_id = e.target.value;

    this.setState(function(prevState) {
      return {
        currentValue: {
          ...prevState.currentValue,
          project_id: project_id
        }
      };
    });
  }


  // Input Change Handler
  changeHandler(event) {
    this.setState({
      [event.target.name]: event.target.value,
    });
  }

  getID(id) {
    EditBiosample.get(id)
      .then(response => {
        this.setState({
          currentValue: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateTutorial() {
    EditBiosample.update(
      this.state.currentValue.id,
      this.state.currentValue
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The data was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }







  // Submit Form
  // submitForm() {
  //   var id = this.props.match.params.id;
  //   fetch("http://127.0.0.1:8000/api/biosample/" + id + "/", {
  //     method: "PUT",
  //     body: JSON.stringify(currentValue),
  //     headers: {
  //       "Content-type": "application/json; charset=UTF-8",
  //     },
  //   })
  //     .then((response) => response.json())
  //     .then((data) => console.log(data));
  // }

  // fetchData() {
  //   var id = this.props.match.params.id;
  //   fetch("http://127.0.0.1:8000/api/biosample/" + id)
  //     .then((response) => response.json())
  //     .then((data) => {
  //       this.setState({
  //         biosample_id: data.biosample_id,
  //         biosample_type: data.biosample_type,
  //         biosample_name: data.biosample_name,
  //         biosample_path: data.biosample_path,
  //         library_id: data.library_id,
  //         biosample_created_on: data.biosample_created_on,
  //         project_id: data.project_id,
  //       });
  //     });
  // }

  // componentDidMount() {
  //   this.fetchData();
  // }

  render() {
    const { currentValue } = currentValue;
    return (
      <div>
        <div className="page-header">
          <h3 className="page-projectName"> Adding BioSample Form </h3>
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item">
                <a href="!#" onClick={(event) => event.preventDefault()}></a>
              </li>
            </ol>
          </nav>
        </div>

        <div className=" col-11 grid-margin stretch-card-1 ">
          <div className="card">
            <div className="card-body">
              <form className="forms-sample" onClick={this.submitForm}>
                <Form.Group>
                  <label htmlFor="exampleInputUsername1">BioSample Id</label>
                  <Form.Control
                    type="text"
                    id1="exampleInputUsername1"
                    placeholder="project Name"
                    size="lg"
                    value={currentValue.biosample_id}
                    id="biosample_id"
                    onChange={this.onChangebiosample_id}
                    required
                  />
                </Form.Group>
                <Form.Group>
                  <label htmlFor="exampleInputUsername1">BioSample Type</label>
                  <Form.Control
                    type="text"
                    id1="exampleInputUsername1"
                    placeholder="project Name"
                    size="lg"
                    value={currentValue.biosample_type}
                    id="biosample_type"
                    onChange={this.onChangebiosample_type}
                    required
                  />
                </Form.Group>
                <Form.Group>
                  <label id1="inputGroupFile01">BioSample Name</label>
                  <input
                    type="text"
                    className="form-control"
                    id1="inputGroupFile01"
                    required
                    value={currentValue.biosample_name}
                    id="biosample_name"
                    onChange={this.onChangebiosample_name}
                  />
                </Form.Group>
                <Form.Group>
                  <label id1="inputGroupFile02">BioSample Path</label>
                  <input
                    type="text"
                    className="form-control"
                    id1="inputGroupFile02"
                    value={currentValue.biosample_path}
                    id="biosample_path"
                    onChange={this.onChangebiosample_path}
                    required
                  />
                </Form.Group>
                <Form.Group>
                  <label id="inputGroupFile03">Library Id</label>
                  <input
                    type="text"
                    className="form-control"
                    id1="inputGroupFile03"
                    value={currentValue.library_id}
                    id="library_id"
                    onChange={this.onChangelibrary_id}
                    required
                  />
                </Form.Group>
                <Form.Group>
                  <label id1="inputGroupFile04">BioSample Created On</label>
                  <input
                    type="date"
                    className="form-control"
                    id1="inputGroupFile04"
                    value={currentValue.biosample_created_on}
                    id="biosample_created_on"
                    onChange={this.onChangebiosample_created_on}
                    required
                  />
                </Form.Group>

                <Form.Group>
                  <label id1="inputGroupFile04">Project Id</label>
                  <input
                    type="text"
                    className="form-control"
                    id1="inputGroupFile04"
                    value={currentValue.project_id}
                    id="project_id"
                    onChange={this.onChangeproject_id}
                    required
                  />
                </Form.Group>

                <div className="col text-center">
                  <button
                  onClick={this.updateTutorial}
                    type="submit"
                    className="btn  mr-2 btn-sm"
                    style={{ backgroundColor: "#fec107" }}
                  >
                    Submit
                  </button>
                </div>
              </form>










              <div className="border border-light p-3 mb-4">
                <div className="text-center">
                  <span>
                    <Link to="/basic-ui/Basespace">
                      <button className="btn btn-light btn-sm"> Cancel</button>
                    </Link>
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Update;
