import axios from "axios";
import {Link} from 'react-router-dom'
function Update() {
  const history = useHistory();
  const { id } = useParams();
  const initialFormData = Object.freeze({
    id: "",
    biosample_id: "",
    biosample_type: "",
    biosample_name: "",
    biosample_path: "",
    library_id: "",
    biosample_created_on: "",
    project_id: "",
  });

  const [formData, updateFormData] = useState(initialFormData);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/biosamples/" + id)
      .then((res) => {
        updateFormData({
          ...formData,
          ["biosample_id"]: res.data.biosample_id,
          ["biosample_type"]: res.data.biosample_type,
          ["biosample_name"]: res.data.biosample_name,
          ["biosample_path"]: res.data.biosample_path,

          ["library_id"]: res.data.library_id,
          ["biosample_created_on"]: res.data.biosample_created_on,
          ["project_id"]: res.data.project_id,
        });
        console.log(res.data);
      });
  }, [updateFormData]);

  const handleChange = (e) => {
    updateFormData({
      ...formData,
      // Trimming any whitespace
      [e.target.name]: e.target.value.trim(),
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);

    axios.put(`http:127.0.0.1:8000/api/biosamples/` + id + "/", {
      biosample_id: formData.biosample_id,
      biosample_type: formData.biosample_type,

      biosample_name: formData.biosample_name,
      biosample_path: formData.biosample_path,

      library_id: formData.library_id,
      biosample_created_on: formData.biosample_created_on,
      project_id: formData.project_id,
    });
    history.push({
      pathname: "/basespace/biosample",
    });
    window.location.reload();
  };

  return (
    <div>
      <div className="page-header">
        <h3 className="page-projectName"> Updating Biosample Form </h3>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb">
            <li className="breadcrumb-item">
              <a href="!#" onClick={(event) => event.preventDefault()}></a>
            </li>
          </ol>
        </nav>
      </div>
      <div className=" col-11 grid-margin stretch-card-1 ">
        <div className="card">
          <div className="card-body">
            <div className="submit-form">
              <div>
                <div className="form-group">
                  <label htmlFor="name">Biosample Id</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    required
                    value={formData.biosample_id}
                    onChange={handleChange}
                    name="biosample_id"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="name">Biosample Type</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    required
                    value={formData.biosample_type}
                    onChange={handleChange}
                    name="biosample_type"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="name">Biosample Name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    required
                    value={formData.biosample_name}
                    onChange={handleChange}
                    name="biosample_name"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="name">Biosample Path</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    required
                    value={formData.biosample_path}
                    onChange={handleChange}
                    name="biosample_path"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="name">Biosample Created On</label>
                  <input
                    type="date"
                    className="form-control"
                    id="name"
                    required
                    value={formData.biosample_created_on}
                    onChange={handleChange}
                    name="biosample_created_on"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="name">Library Id</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    required
                    value={formData.library_id}
                    onChange={handleChange}
                    name="library_id"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="name">Project Name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="name"
                    placeholder="Project Name"
                    required
                    value={formData.project_id}
                    onChange={handleChange}
                    name="project_id"
                  />
                </div>
                <div className="col text-center">
                  <button
                    onClick={handleSubmit}
