import React from 'react';
class Update extends React.Component{
    constructor(){
        super();
        this.state={
            biosample_id: "",
            biosample_type: "",
            biosample_name: "",
            biosample_path: "",
            library_id: "",
            biosample_created_on: "",
            project_id: "",
        }
        this.changeHandler=this.changeHandler.bind(this);
        this.submitForm=this.submitForm.bind(this);
    }

    // Input Change Handler
    changeHandler(event){
        this.setState({
            [event.target.name]:event.target.value
        });
    }

    // Submit Form
    submitForm(){
        var id=this.props.match.params.id;
        fetch('http://127.0.0.1:8000/api/biosample/'+id+'/',{
            method:'PUT',
            body:JSON.stringify(this.state),
            headers:{
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
        .then(response=>response.json())
        .then((data)=>console.log(data));
    }

    fetchData(){
        var id=this.props.match.params.id;
        fetch('http://127.0.0.1:8000/api/biosample/'+id)
        .then(response=>response.json())
        .then((data)=>{
            this.setState({
                biosample_id: data.biosample_id,
                biosample_type: data.biosample_type,
                biosample_name: data.biosample_name,
                biosample_path: data.biosample_path,
                library_id: data.library_id,
                biosample_created_on: data.biosample_created_on,
                project_id: data.project_id

            });
        });
    }

    componentDidMount(){
        this.fetchData();
    }

    render(){
        return (
            <table className="table table-bordered">
                <tbody>
                    <tr>
                        <th>biosample_id</th>
                        <td>
                            <input value={this.state.biosample_id} name="biosample_id" onChange={this.changeHandler} type="text" className="form-control" />
                        </td>
                    </tr>
                    <tr>
                        <th>biosample_type</th>
                        <td>
                            <input value={this.state.biosample_type} name="biosample_type" onChange={this.changeHandler} type="text" className="form-control" />
                        </td>
                    </tr>
                    <tr>
                        <th>biosample_name</th>
                        <td>
                            <input value={this.state.biosample_name} name="biosample_name" onChange={this.changeHandler} type="text" className="form-control" />
                        </td>
                    </tr>
                    <tr>
                        <th>biosample_path</th>
                        <td>
                            <input value={this.state.biosample_path} name="biosample_path" onChange={this.changeHandler} type="text" className="form-control" />
                        </td>
                    </tr>
                    <tr>
                        <th>library_id</th>
                        <td>
                            <input value={this.state.library_id} name="library_id" onChange={this.changeHandler} type="text" className="form-control" />
                        </td>
                    </tr>
                    <tr>
                        <th>biosample_created_on</th>
                        <td>
                            <input value={this.state.biosample_created_on} name="biosample_created_on" onChange={this.changeHandler} type="date" className="form-control" />
                        </td>
                    </tr>
                    <tr>
                        <th>project_id</th>
                        <td>
                            <input value={this.state.project_id} name="project_id" onChange={this.changeHandler} type="date" className="form-control" />
                        </td>
                    </tr>
                    <tr>
                        <td colSpan="2">
                            <input type="submit" onClick={this.submitForm} className="btn btn-dark" />
                        </td>
                    </tr>
                </tbody>
            </table>
        );
    }
}

export default Update;
