import React, { Component, Fragment } from "react";
import { withAlert } from "react-alert";
import { connect } from "react-redux";
import PropTypes from "prop-types";

export class Alerts extends Component {
  static propTypes = {
    // error: PropTypes.object.isRequired,
    message: PropTypes.object.isRequired,
  };

  componentDidUpdate(prevProps) {
    const { message,alert } = this.props;

    if (message !== prevProps.message) {
      if (message.addBasespace) alert.success(message.addBasespace);

      if (message.addBiosample) alert.success(message.addBiosample);

      if (message.addAnalysis) alert.success(message.addAnalysis);
    }
  }

  render() {
    return <Fragment />;
  }
}

const mapStateToProps = (state) => ({
  // error: state.errors,
  message: state.messages,
});

export default connect(mapStateToProps)(withAlert()(Alerts));
